package jan20;

public class Evenoddno {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				
		for (int x=1; x<=10; x++) {
			
			if((x % 2)== 0) {
			
				System.out.println("Even no is="+ x);
			}
			
			else {
			
				System.out.println("               "+"odd no is= "+ x);
			}

		}

	}

}
